import React, { Component } from "react";
import FriendCard from "./components/FriendCard";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import friends from "./friends.json";

class App extends Component {
  // Setting this.state.friends to the friends json array
  state = {
    friends,
    topscore: 0,
    score: 0
  };

  shuffleFriend = id => {
   this.state.friends.find((clicked, i) => {
     if(clicked.id === id) {
       if(friends[i].clicked === false){
        friends[i].clicked = true
       }
     }
   })
    this.setState({friends, score: this.state.score +1});
   friends.sort(() => Math.random() -0.5);
   this.setState.score++;
   this.setState.topscore ++;
   this.setState.clicked = true
  };

  // Map over this.state.friends and render a FriendCard component for each friend object
  render() {
    return (
      <Wrapper>
        <Title>Score: {this.state.score} <strong>||</strong> Top Score: {this.state.topscore}</Title>
        {this.state.friends.map(friend => (
          <FriendCard
            shuffleFriend={this.shuffleFriend}
            id={friend.id}
            key={friend.id}
            name={friend.name}
            image={friend.image}
            occupation={friend.occupation}
            location={friend.location}
            clicked = {friend.clicked}
          />
        ))}
      </Wrapper>
    );
  }
}

export default App;
